DELIMITER $$
CREATE TRIGGER `validate_sensor_data` BEFORE INSERT ON `sensor_data` FOR EACH ROW BEGIN
    -- Validasi range temperature
    IF NEW.temperature_kamar < -50 OR NEW.temperature_kamar > 100 THEN
        SET NEW.temperature_kamar = 0;
    END IF;
    
    IF NEW.temperature_rtamu < -50 OR NEW.temperature_rtamu > 100 THEN
        SET NEW.temperature_rtamu = 0;
    END IF;
    
    -- Validasi range humidity
    IF NEW.humidity_kamar < 0 OR NEW.humidity_kamar > 100 THEN
        SET NEW.humidity_kamar = 0;
    END IF;
    
    IF NEW.humidity_rtamu < 0 OR NEW.humidity_rtamu > 100 THEN
        SET NEW.humidity_rtamu = 0;
    END IF;
    
    -- Validasi battery
    IF NEW.battery_voltage < 0 OR NEW.battery_voltage > 15 THEN
        SET NEW.battery_voltage = 12.6;
    END IF;
    
    IF NEW.battery_soc < 0 OR NEW.battery_soc > 100 THEN
        SET NEW.battery_soc = 0;
    END IF;
END
$$
DELIMITER ;

